INSERT INTO `wp_wfNotifications` VALUES ('network-GD',0,'gravityscan',300,1498505147,'<p><strong>Say hello to Gravityscan, the free malware and vulnerability scanner for any website</strong></p>\n\n<p>Today we are excited to share a new product we have been working on for the last year. It is free to use, works on any website and you don’t need to install anything to use it. We think you’ll find it to be a great complement to the WordPress protection you’re already getting with Wordfence. And we know that for a lot of you WordPress isn’t the only platform you need to secure, so we built Gravityscan to work with all of your sites.</p>\n\n<p><a href=\"https://www.gravityscan.com?utm_source=plugin&utm_medium=pluginalert&utm_campaign=la061817\" target=\"_blank\" class=\"wf-btn wf-btn-primary\">Scan your site now</a></p>','[]'),('network-GE',0,'siteaudit',400,1498505147,'<p><strong>Announcing New Site Security Audit Service</strong></p>\n<p>We are very excited to announce that we have officially launched a new WordPress Site Security Audit Service.</p>\n<p>We have been providing this service to select customers for several weeks prior to the official launch. We are now live with the service and making it available to all of our customers.</p>\n<p>We have written about the service on our blog, where we explain a few of the benefits of having our dedicated Security Services Team evaluate and secure your site.</p>\n<p><a href=\"https://www.wordfence.com/blog/2017/05/wordpress-security-audit/?utm_source=plugin&utm_medium=pluginalert&utm_campaign=sa052517\" target=\"_blank\">You can find the full announcement on our blog...</a></p>','[]'),('network-GF',0,'gravityscanbadge',500,1501358558,'<p><strong style=\"color:#00709E;\">Introducing a Free Trust Badge With Blacklist and SEO Monitoring</strong></p>\n<p>Today we are excited to officially launch the Gravityscan trust badge program. As you know, Gravityscan is a project by the Wordfence team to bring malware and vulnerability scanning to all websites. Until now, daily monitoring had been a paid feature of Gravityscan, priced at around $120 per year.  The new badge program provides free daily security monitoring for any website.</p>\n<p>The Gravityscan trust badge checks your website daily against over 20 blacklists and ensures that your content does not contain links to malicious sites. The program also includes a comprehensive daily scan for malware.</p>\n<p>The Gravityscan trust badge also helps improve conversion rates and visitor confidence in your website. To learn more about this exciting and completely free program, you can find the <a href=\"https://www.gravityscan.com/blog/gravityscan-badge?utm_source=plugin&utm_medium=pluginalert&utm_campaign=072617\" target=\"_blank\">full details on the Gravityscan blog</a>.</p>\n<p class=\"wf-center\"><a href=\"https://www.gravityscan.com/sign-up?plan=free-monitoring&utm_source=plugin&utm_medium=pluginalert&utm_campaign=072617\" target=\"_blank\" style=\"text-align: center;text-transform: uppercase;font-weight: bold;background-color: #11967A;height: 44px;line-height: 44px;padding: 0px 20px;color: #ffffff;text-decoration: none;display: inline-block;border-radius: 3px;\">Get Free Trust Badge</a></p>','[]'),('network-GG',0,'blogpost082917',400,1505419440,'<p><strong>Better Data Means Better Protection</strong></p>\n<p>Mission-critical sites rely on the real-time protection provided by Wordfence Premium. Learn how the 30-strong team at Wordfence stays on top of the latest threats, providing Premium customers with the real-time threat intelligence they need to stay safe.</p>\n<p><a href=\"https://www.wordfence.com/blog/2017/08/benefits-wordfence-premium/?utm_source=plugin&amp;utm_medium=pluginalert&amp;utm_campaign=bp082917\" class=\"wf-btn wf-btn-callout wf-btn-primary\" target=\"_blank\">Learn More</a></p>','[]'),('site-AEAAAAA',0,'wfplugin_updates',502,1498678070,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">An update is available for 1 plugin</a>','[]'),('site-AIAAAAA',0,'wfplugin_scan',502,1499691934,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">2 issues found in most recent scan</a>','[]'),('site-AMAAAAA',0,'wfplugin_updates',502,1499630434,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">Updates are available for 2 plugins</a>','[]'),('site-C4AQAAA',0,'wfplugin_scan',502,1507065207,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-CQAAAAA',0,'wfplugin_scan',502,1499960866,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-CUAAAAA',0,'wfplugin_scan',502,1500147250,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-CUAQAAA',0,'wfplugin_scan',502,1506446521,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-CYAAAAA',0,'wfplugin_updates',502,1500147250,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">An update is available for 1 plugin</a>','[]'),('site-CYAQAAA',0,'wfplugin_updates',502,1507065206,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">An update is available for 1 plugin</a>','[]'),('site-D4AQAAA',0,'wfplugin_scan',502,1509483504,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-DAAQAAA',0,'wfplugin_updates',502,1507756675,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">An update is available for 1 plugin</a>','[]'),('site-DEAQAAA',0,'wfplugin_scan',502,1507756675,'<a href=\"https://www.ephorsec.org/wp-admin/admin.php?page=WordfenceScan\">1 issue found in most recent scan</a>','[]'),('site-DQAAAAA',0,'wfplugin_updates',502,1506319522,'<a href=\"https://www.ephorsec.org/wp-admin/update-core.php\">An update is avai